    .header,
    .footer {
        height: 100px;
        background-color: #000;
    }
    .parent {
        height: 300px;
        background-color: #eee;

    }
    .left {
        width: 300px;
        height: 300px;
        background-color: #00ffff;

        float: left;
        margin-left: -100%;
    }
    .center {
        width: 100%;
        height: 300px;
        background-color: #ffff00;

        float: left;
    }
