    el: "#app",
    data: {
      age: 10,
      radioVal: "0",
      checkVal: false,
      checkArr: [],
      selectVal: 0,
      selectArr: [],
    },
  });
</script>

[ 事件绑定 ]

<div id="app">
  <!-- 冒泡: 由内到外   捕获 : 由外到内 -->

  <!-- 采用捕获 ，其他元素还是采用冒泡的方式 -->
  <!-- <div @click.capture="parent"> -->
  <div @click="parent">
    parent
    <!-- @click.stop 阻止冒泡 -->
    <div @click.stop="son">
      son
      <!-- prevent 阻止默认行为  self 点击自己的时候才会触发 once 只触发一次 passive 表示阻止默认行为 不能与 prevent 同时使用 -->
      <div @click.self="grandSon">grandSon</div>
    </div>
  </div>
</div>

<script>
  let vm = new Vue({
    el: "#app",
    data: {},
    methods: {
      // 绑定事件 事件名不要和属性名相同
      parent() {
        alert("parent");
      },
      son() {
        alert("son");
      },
      grandSon() {
        alert("grandSon");
      },
    },
  });
</script>

[ 自定义指令 ]

<!-- 自定义指令 -->
<div class="custom">
  <p>
    <input type="text" v-focus.color="col" />
  </p>
</div>

<script>
  //  `v-focus` 全局自定义指令
  Vue.directive("focus", {
    // 指令初始化时调用
    bind() {
      console.log("初始化");
    },
    // 当被绑定的元素插入到 DOM 中时……
    inserted: function (el, bindings, vnode) {
      // el 绑定的元素,bindings 指令的参数,vnode 虚拟节点

      console.log(bindings);
      // 判断有无修饰符 如果有 进行操作
      if (bindings.modifiers.color) {
        el.style.background = bindings.value;
      }
      Vue.nextTick(() => {
        // 聚焦元素
        el.focus();
      });
    },
    updata() {
      console.log("更新时触发");
    },
    unbind() {
      console.log("指令销毁 指令和绑定元素解绑时");
    },
  });

  let vm = new Vue({
    el: "#app",
    data: {
      col: "red",
    },
    methods: {
      // 局部自定义指令 只在当前 实例起作用
      directives: {
        focus: {
          // 指令初始化时调用
          bind() {
            console.log("初始化");
          },
          // 当被绑定的元素插入到 DOM 中时……
          inserted: function (el, bindings, vnode) {
            // el 绑定的元素,bindings 指令的参数,vnode 虚拟节点

            console.log(bindings);
            // 判断有无修饰符 如果有 进行操作
            if (bindings.modifiers.color) {
              el.style.background = bindings.value;
            }
            Vue.nextTick(() => {
              // 聚焦元素
              el.focus();
            });
          },
          updata() {
            console.log("更新时触发");
          },
          unbind() {
            console.log("指令销毁 指令和绑定元素解绑时");
          },
        },
      },
    },
  });
</script>
