        <li v-for="(item,index) in lists" :key="`${index}_a`" ref="listArray">
          <img :src="item.cover" alt="" />
          <button @click="addCart(index)">加入购物车</button>
        </li>
      </ul>
      <transition @enter="enter" @after-enter="after">
        <span v-if="isShow"></span>
      </transition>
      <div class="cart" ref="cart">购物车</div>
    </div>
  </body>
</html>
<script src="vue.min.js"></script>
<script>
  let vm = new Vue({
    el: "#app",
    data: {
      currentIndex: -1, // 加入购物车的默认索引
      isShow: false,

      lists: [
        {
          id: 1,
          cover: "http://www.javascript.cn/203853de0f7a753037.png",
        },
        {
          id: 2,
          cover: "http://www.javascript.cn/202711f70eea987000.jpg",
        },
        {
          id: 3,
          cover: "http://www.javascript.cn/211735f96b30549920.jpg",
        },
      ],
    },
    methods: {
      addCart(index) {
        // console.log('加入购物车',index);
        this.currentIndex = index;
        this.isShow = true;
        // console.log(this.currentIndex);
      },
      enter(el, done) {
        // el 表示当前的动画元素 done 表示动画执行完成后的回调函数

        //  确定点击元素 li 的位置 ref= "listArray" 专门用来获取 dom 元素
        // console.log(this.$refs.listArray,111);
        let oLi = this.$refs.listArray[this.currentIndex];
        // console.log(oLi,222);
        let { x, y } = oLi.getBoundingClientRect(); //
        //  设置 span 动画元素的位置 与点击元素 li 位置一样
        // console.log(x,y);
        el.style.left = x + "px";
        el.style.top = y + "px";
        el.style.background = `url(${this.lists[this.currentIndex].cover})`;
        el.style.backgroundSize = "100% 100%";
        //  求出购物车的位置
        let Cart = this.$refs.cart;
        let { x: a, y: b } = Cart.getBoundingClientRect(); // x, y 重新命名 a,b
        //  动画元素到购物车移动的距离
        el.style.transform = `translate3d(${a - x}px,${b - y}px,0) scale(0)`;

        el.addEventListener("transitionend", done, false);
      },
      after() {
        this.isShow = false;
      },
    },
  });
</script>
