    return (
      <button onClick={this.handleSubmit}>Submit</button>
    )
  }
} 3.使用带有 Hooks 的函数组件
const SubmitButton = () => {
  const [isFormSubmitted, setIsFormSubmitted] = useState(false);
  return (
    <button onClick={() => {
